// eslint-disable-next-line jsx-a11y/img-redundant-alt
import React, {useState} from "react";
import { NavLink } from "react-router-dom";
import { useSelector } from "react-redux";
import styles from "./navBar.module.css";
import img from "../../Images/06.png";

const NavBar = () => {

  const [filteredDogs, setFilteredDogs] = useState([]); // Estado local para la lista filtrada
  const allCharacters = useSelector((state) => state.charactersDogs);

  const handleInputChange = (event) => {
    const searchTerm = event.target.value;
    if (searchTerm === "") {
      // Si el input está vacío, muestra todos los perros
      setFilteredDogs([]);
    } else {
      // Si hay un valor en el input, filtra la lista de perros
      const filtered = allCharacters.filter((dog) =>
        dog.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredDogs(filtered);
    }
  };

  return (
    <div className={styles.containerNav}>
      <div className={styles.containerTitle}>
        <img className={styles.image} src={img} alt="Imagen" />
        <h1 className={styles.title}>Dogs Web</h1>
      </div>
      <div className={styles.containerSearch}>
        <input
          type="text"
          className={styles.searchBar}
          onChange={handleInputChange}
          placeholder="Find dog breeds"
        />
        <NavLink to="/">
          <button className={styles.exitPage}></button>
        </NavLink>
      </div>
    </div>
  );
};

export default NavBar;
