/* eslint-disable no-sequences */
import React from "react";
import { useState, useEffect } from "react";
import axios from 'axios';
import styles from "./dogsCards.module.css";

const itemsPerPage = 8;

const DogCards = () => {
    const [itemList, setItemList] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);

    useEffect(() => {
      axios.get('http://localhost:3001/dogs')
        .then(response => {
          setItemList(response.data);
        })
        .catch(error => {
          console.error('Error fetching data:', error);
        });
    }, []);

    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = itemList.slice(indexOfFirstItem, indexOfLastItem);

    const totalPages = Math.ceil(itemList.length / itemsPerPage);

  const nextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };
  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className={styles.containerCards}>
      <ul>
        {currentItems?.map((item) => (
          <div>Nombre: {item.name}</div>,
          <div>Imagen: {item.imagen}</div>,
          <div>Temperament: {item.temperament}</div>,
          <div>Peso: {item.weight.imperial} / {item.weight.metric}</div>
        ))}
      </ul>
      <div>
        <button onClick={prevPage} disabled={currentPage === 1}>
          Anterior
        </button>
        <span>
          PÃ¡gina {currentPage} de {totalPages}
        </span>
        <button onClick={nextPage} disabled={currentPage === totalPages}>
          Siguiente
        </button>
      </div>
    </div>
  );
};

export default DogCards;
