import { GETDOGS, GETDOGBYID, GETDOGSBYNAME, ORDER } from "../actions/types";

const initialState = {
  charactersDogs: [],
  characterDog: [],
  searchTerm: [],
  searchResults: []
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GETDOGS:
      return {
        ...state,
        charactersDogs: action.payload,
      };
    case GETDOGBYID:
      return {
        ...state,
        characterDog: action.payload,
      };
    case GETDOGSBYNAME:
      const searchTerm = action.payload;
      const searchResults = state.charactersDogs.filter(dog =>
        searchTerm.some(searchItem =>
          dog.name && searchItem.name &&
          dog.name.toLowerCase().includes(searchItem.name.toLowerCase())
        )
      );
      return {
        ...state,
        searchTerm,
        searchResults
      };
    case ORDER:
      const allCharactersFavCopy = [...state.charactersDogs];
      return {
        ...state,
        charactersDogs:
          action.payload === "A"
            ? allCharactersFavCopy.sort((a, b) => a.id - b.id)
            : allCharactersFavCopy.sort((a, b) => b.id - a.id),
      };
    default:
      return { ...state };
  }
};

export default rootReducer;
