import React, { useEffect } from "react";
import { useParams , useHistory} from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getDogById } from "../../redux/actions";
import styles from "./DogDetails.module.css";

const DogDetails = () => {
  const { id } = useParams();
  const history = useHistory();
  const dispatch = useDispatch();
  const characterDog = useSelector((state) => state.characterDog);

  useEffect(() => {
    dispatch(getDogById(id));
  }, [dispatch, id]);

  const handleClose = () => {
    history.goBack(); // Funci칩n para volver atr치s en el historial
  };

  return (
    <div className={styles.containerDetails}>
      {characterDog[0] ? (
        <div className={styles.detailsCard}>
          <h1>{characterDog[0].name}</h1>
          <img src={characterDog[0]?.image} alt="Dog" />
          <h2>Life Span: {characterDog[0].life_span}</h2>
          <h2>Temperament: {characterDog[0].temperament}</h2>
          <h2>
            Weight: {characterDog[0]?.weight}
          </h2>
          <h2>
            Height: {characterDog[0]?.height} in /{" "}
            {characterDog[0]?.height} cm
          </h2>
          <button className={styles.goBackButton} onClick={handleClose}>
            Close
          </button>
        </div>
      ) : null}
    </div>
  );
};

export default DogDetails;
