import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getTemperament, filterTemperaments } from "../../redux/actions";
import styles from "./Form.module.css";

const Form = () => {
  const dispatch = useDispatch();
  const allTemperament = useSelector((state) => state.filteredTemperament);

  useEffect(() => {
    dispatch(getTemperament());
  }, [dispatch]);

  const [selectedValues, setSelectedValues] = useState([]);

  const handleSelectChange = (event) => {
    const selectedOptions = Array.from(event.target.selectedOptions, option => option.value);
    setSelectedValues(selectedOptions);
  };

  const handleRemoveOption = (valueToRemove) => {
    const updatedValues = selectedValues.filter(value => value !== valueToRemove);
    setSelectedValues(updatedValues);
  };

  return (
    <div className={styles.containerForm}>
      <form>
        <div className={styles.containerTitle}>
          <h1>Form</h1>
          <button>X</button>
        </div>
        <div className={styles.containerLeft}>
          <label htmlFor="name">name</label>
          <input name="name" type="text" />
        </div>
        <div className={styles.containerRight}>
          <label htmlFor="height">height</label>
          <input name="height" type="text" />
        </div>
        <div className={styles.containerLeft}>
          <label htmlFor="width">width</label>
          <input name="width" type="text" />
        </div>
        <div className={styles.containerRight}>
          <label htmlFor="yearsOfLife">yearsOfLife</label>
          <input name="yearsOfLife" type="text" />
        </div>
        <div className={styles.containerLeft}>
          <label htmlFor="image">Image</label>
          <input type="text" />
        </div>
        <div className={styles.containerRight}>
          <label>Temperment</label>
          <select onChange={handleSelectChange}>
            {allTemperament.map((temp) => {
              return <option value={temp.name}>{temp.name}</option>;
            })}
          </select>
        </div>
        <div>
        <p>Selecciones:</p>
        {selectedValues.length > 0 ? (
          <ul>
            {selectedValues.map((value, index) => (
              <li key={index}>{value}</li>
            ))}
          </ul>
        ) : (
          <p>No hay selecciones.</p>
        )}
      </div>
        <button>Create</button>
      </form>
    </div>
  );
};

export default Form;
