import { GETDOGS, GETDOGBYID, ORDER } from "../actions/types";

const initialState = {
  charactersDogs: [],
  characterDog: []
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GETDOGS:
      return {
        ...state,
        charactersDogs: action.payload,
      };
    case GETDOGBYID:
      return {
        ...state,
        characterDog: action.payload,
      }
    case ORDER:
        const allCharactersFavCopy = [...state.allCharacters];
        return {
          ...state,
          myFavorites:
            action.payload === "A"
              ? allCharactersFavCopy.sort((a, b) => a.id - b.id)
              : allCharactersFavCopy.sort((a, b) => b.id - a.id),
        };
    default:
      return { ...state };
  }
};

export default rootReducer;
