import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDogs } from "../../redux/actions/index";
import styles from "./DogsCards.module.css";
import DogCard from "../DogCard/DogCard";
import FilterTemperament from "../FilterTemperament/FilterTemperament";

const itemsPerPage = 8;

const DogsCards = () => {
  const [currentPage, setCurrentPage] = useState(1);
  const [order, setOrder] = useState("asc"); // Estado para el tipo de ordenamiento

  const dispatch = useDispatch();
  const allCharacters = useSelector((state) => state.charactersDogs);

  useEffect(() => {
    dispatch(getDogs());
  }, [dispatch]);

  const handleOrder = () => {
    setOrder(order === "asc" ? "desc" : "asc"); // Cambiar entre asc y desc
  };

  const dogsToDisplay = [...allCharacters];

  // Ordenar los perros según el peso y el tipo de orden
  dogsToDisplay.sort((a, b) => {
    if (order === "asc") {
      return a.weight.metric - b.weight.metric;
    } else {
      return b.weight.metric - a.weight.metric;
    }
  });

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const current = dogsToDisplay.slice(indexOfFirstItem, indexOfLastItem);

  const totalPages = Math.ceil(dogsToDisplay.length / itemsPerPage);

  const nextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className={styles.containerCards}>
      <div className={styles.containerSelect}>
        <FilterTemperament />
        <button onClick={handleOrder}>
          {order === "asc" ? "Order Asc" : "Order Desc"}
        </button>
      </div>
      <div className={styles.cards}>
        {current?.map((data) => {
          return (
            <DogCard
              id={data.id}
              name={data.name}
              image={data.image.url}
              temperament={data.temperament}
              weight={data.weight}
            />
          );
        })}
      </div>
      <div className={styles.containerPaginated}>
        <button
          className={styles.buttonPrev}
          onClick={prevPage}
          disabled={currentPage === 1}
        ></button>
        <span className={styles.currentPage}>
          {currentPage} of {totalPages}
        </span>
        <button
          className={styles.buttonNext}
          onClick={nextPage}
          disabled={currentPage === totalPages}
        ></button>
      </div>
    </div>
  );
};

export default DogsCards;
